define('pgui.editors/image_uploader',['pgui.editors/plain'], function (PlainEditor) {
    return PlainEditor.extend({
        init: function (rootElement, readyCallback) {
            this._super(rootElement, readyCallback);

            var $formGroup = rootElement.closest('.form-group');
            var $btnGroup = $formGroup.find('.btn-group[data-toggle-name]');
            var $toggleTarget = $formGroup.find('[name=' + $btnGroup.data('toggle-name') + ']');

            this.$replaceButton = $btnGroup.find('.js-replace').first();
            this.$image = $formGroup.find('img').first();
            this.isImageBased = this.$image.length == 1;
            this.oldImageSource = this.$image.attr('src');
            this.newImageSource = this.oldImageSource;

            var self = this;

            $btnGroup.on('click', 'button', function () {
                $btnGroup.find('button').removeClass('active');
                var $button = $(this).addClass('active');
                $toggleTarget.val($button.val());
                if (self.isImageBased) {
                    self._setImageProperties($button.val());
                }
            });

            $btnGroup.find('button[value=' + $toggleTarget.val() + ']').addClass('active');
        },

        doChanged: function() {
            if (this.isImageBased) {
                this._processNewImage();
            } else {
                this.$replaceButton.prop('disabled', false);
                this.$replaceButton.click();
            }
            this._super();
        },

        _setImageProperties: function(action) {
            this.$image.toggle(action != 'Remove');
            if (action == 'Keep') {
                this.$image.attr('src', this.oldImageSource);
            } else if (action == 'Replace') {
                this.$image.attr('src', this.newImageSource);
            }
        },

        _processNewImage: function() {
            var self = this;
            var reader  = new FileReader();
            reader.addEventListener("load", function () {
                self.newImageSource = reader.result;
                self.$replaceButton.prop('disabled', false);
                self.$replaceButton.click();
            }, false);

            var file = this.rootElement.get(0).files[0];
            if (file) {
                reader.readAsDataURL(file);
            }
        }
    });
});

define('pgui.editors/multivalue_select',[
    'underscore',
    'pgui.editors/custom',
    'libs/select2',
    'locales/select2_locale'
], function (_, CustomEditor) {

    return CustomEditor.extend({
        init: function (rootElement, readyCallback) {
            this._super(rootElement, readyCallback);
            var self = this;
            var $el = $(rootElement);
            var maxSelectionSize = $el.attr('data-max-selection-size');
            var placeholder = $el.attr('data-placeholder');

            $el.select2({
                width: '100%',
                maximumSelectionSize: maxSelectionSize,
                placeholder: placeholder
            });

            // use readonly emulation for <select>
            if ($el.attr('readonly')) {
                this.setReadonly(true);
            }

            $('label[for=' + $el.attr('id') + ']').on('click', function () {
                $el.select2('focus');
            });

            $el.on('change', function () {
                if (typeof $el.valid === 'function' ) {
                    var $form  = $(this.form);
                    if ($form.length && _.keys($form.data('validator').submitted).length) {
                        $el.valid();
                    }
                }

                self.doChanged();
            });
        },

    	getValue: function () {
    		return this.rootElement.select2("val");
    	},

        getDisplayValue: function () {
            return _.map(this.rootElement.select2('data'), function (item) {
                return item.text;
            });
        },

    	setValue: function (value) {
            if (value.split) {
                this.rootElement.select2("val", value.split(","));
            } else if (_.isArray(value)) {
                this.rootElement.select2("val", value);
            } else {
                console.error("'value' must be string or array, '" + typeof(value) + "' given");
            }

            return this;
    	},

    	getEnabled: function () {
    		return !this.rootElement.prop("disabled");
    	},

    	setEnabled: function (value) {
    		this.rootElement.prop('disabled', !value);
    		this.rootElement.select2('enable', !!value);
            return this;
    	},

    	getReadonly: function () {
    		return !!this.rootElement.data('readonly');
    	},

    	setReadonly: function (value) {
    		this.rootElement.select2('readonly', !!value);
    		this.rootElement.data('readonly', !!value);
            return this;
    	},

    	addItem: function (value, caption) {
    		this.rootElement.append($("<option value='"+value+"'>"+caption+"</option>"));
            return this;
    	},

    	removeItem: function (value) {
    		this.rootElement.find("option[value="+value+"]").remove();
            return this;
    	},

    	getItems: function () {
    		return this.rootElement.find("option").map(function (i, option) {
    			var $option = $(option);
    			return {value: $option.val(), caption: $option.text()};
    		});
    	},

    	getItemCount: function () {
    		return this.rootElement.find("option").length;
    	},

        clear: function() {
            this.rootElement.find("option").remove();
            return this;
        },

        isMultivalue: function () {
            return true;
        }
    });
});

define('pgui.editors/remote_multivalue_select',[
    'underscore',
    'pgui.editors/custom',
    'libs/select2',
    'locales/select2_locale'
], function (_, CustomEditor) {

    return CustomEditor.extend({
        init: function (rootElement, readyCallback) {
            this.queryFunction = function (term) { return {term: term}; };
            this._super(rootElement, readyCallback);

            var $el = $(rootElement);
            var maxSelectionSize = $el.attr('data-max-selection-size');
            var self = this;

            this.bind('submit.pgui.nested-insert', function ($insertButton, primaryKey, record) {
                var currentValue = this.getValue();
                var storedValue = record[$insertButton.data('stored-field-name')];
                currentValue.push(storedValue.toString());
                this.setValue(currentValue);
            }.bind(this));

            $el.on("change", this.doChanged.bind(this));

            $el.select2({
                multiple: true,
                maximumSelectionSize: maxSelectionSize,
                ajax: {
                    url: $el.attr('data-url'),
                    dataType: 'json',
                    results: function (data) {
                        return {
                            results: $.map(data, function (item) {
                                return {id: item.id, text: item.value, fields: item.fields};
                            })
                        };
                    },
                    data: function (term) {
                        return self.queryFunction(term);
                    }
                },
                initSelection: function (element, callback) {
                    if (element.val().length === 0) {
                        callback([]);
                        return;
                    }

                    var ids = element.val().split(',');
                    var results = [];

                    _.each(ids, function (id, i) {
                        $.ajax({
                            url: $el.data("url"),
                            data: {id: id},
                            dataType: "json"
                        }).success(function (data) {
                            $.each(data, function (k, item) {
                                if (item.id == id) {
                                    results.push({
                                        id: item.id,
                                        text: item.value,
                                        fields: item.fields
                                    });

                                    if (results.length === ids.length) {
                                        callback(results);
                                        self.rootElement.trigger('select2-init');
                                    }

                                    return false;
                                }

                            });
                        });
                    });
                },
                width: '100%'
            });
        },

    	getValue: function () {
    		return this.rootElement.select2("val");
    	},

        getDisplayValue: function () {
            return _.map(this.rootElement.select2('data'), function (item) {
                return item.text;
            });
        },

    	setValue: function (value) {
            if (value.split) {
                this.rootElement.select2("val", value.split(","));
            } else if (_.isArray(value)) {
                this.rootElement.select2("val", value);
            } else {
                console.error("'value' must be string or array, '" + typeof(value) + "' given");
            }

            return this;
    	},

    	getEnabled: function () {
    		return !this.rootElement.prop("disabled");
    	},

    	setEnabled: function (value) {
    		this.rootElement.prop('disabled', !value);
    		this.rootElement.select2('enable', !!value);
            return this;
    	},

    	getReadonly: function () {
    		return !!this.rootElement.data('readonly');
    	},

    	setReadonly: function (value) {
    		this.rootElement.select2('readonly', !!value);
    		this.rootElement.data('readonly', !!value);
            return this;
    	},

        isMultivalue: function () {
            return true;
        },

        setQueryFunction: function (fn) {
            this.queryFunction = fn;
            return this;
        }
    });
});

define('pgui.editors/radio',['pgui.editors/custom', 'underscore'], function (CustomEditor, _) {

    var inputTemplateString = '<input'
        + ' type="radio"'
        + ' name="<%=editorName%>"'
        + ' value="<%=value%>">'
        + '<%=caption%>';

    var stackedItemTemplate = _.template(
        '<div class="radio js-value"><label>' + inputTemplateString + '</label></div>'
    );

    var inlineItemTemplate = _.template(
        '<label class="radio-inline js-value">' + inputTemplateString + '</label>'
    );

    return CustomEditor.extend({

        init: function(rootElement, readyCallback) {
            this._super(rootElement, readyCallback);
            this.rootElement.find("input").change(_.bind(function() {
                this.doChanged();
            }, this));

            this.bind('submit.pgui.nested-insert', function ($insertButton, primaryKey, record) {
                this.addItem(
                    primaryKey,
                    record[$insertButton.data('display-field-name')]
                );
                this.setValue(primaryKey);
            }.bind(this));
        },

        getValue: function() {
            return this.rootElement.find("input:checked").val();
        },

        setValue: function(value) {
            this.rootElement.find("input").each(function(i, item) {
                if ($(item).attr('value') == value) {
                    $(item).attr('checked', true);
                }
            });

            return this;
        },

        getEnabled: function() {
            return this.rootElement.find("input:enabled").length > 0;
        },

        setEnabled: function(value) {
            this.rootElement.find("input").each(function(i, item) {
                if (!value) {
                    $(item).attr('disabled', true);
                }
                else {
                    $(item).removeAttr('disabled');
                }
            });

            return this;
        },

        getReadonly: function() {
            return this.rootElement.find("input:enabled").length === 1;
        },

        setReadonly: function(value) {
            this.rootElement.find("input").each(function(i, item) {
                if (value) {
                    if (!($(item).attr('checked'))) {
                        $(item).attr('disabled', true);
                    }
                } else {
                    $(item).removeAttr('disabled');
                }
            });

            return this;
        },

        clear: function() {
            this.rootElement.find(".radio, .radio-inline").remove();
            return this;
        },

        addItem: function(value, caption) {
            var $editor = this.rootElement;
            var isInline = $editor.data('inline');
            var data = {
                editorName: $editor.attr('data-editor-name'),
                value: value,
                caption: caption
            };

            var $newItem = $(isInline ? inlineItemTemplate(data) : stackedItemTemplate(data));

            if (this.getItemCount() > 0) {
                $editor.find('.js-value').last().after($newItem);
            } else {
                $editor.prepend($newItem);
            }

            return this;
        },

        removeItem: function(value) {
            this.rootElement.find("input[value='" + value + "']").parent().remove();
            return this;
        },

        getItems: function() {
            return this.rootElement.find("input").map(function(i, item) {
                return { value: $(item).attr('value'), caption: $(item).closest('label').text() };
            });
        },

        getItemCount: function() {
            return  this.rootElement.find("input").length;
        },

        getCaption: function() {
            return this.rootElement.find("input:checked").parent().text();
        },

        _getSetRequiredTarget: function () {
            return this.rootElement.find('input[type=radio]');
        }
    });

});

