define('pgui.list-page-main',[
    'underscore',
    'pgui.page_settings',
    'pgui.shortcuts',
    'pgui.grid',
    'pgui.filter_common',
    'pgui.filter_builder',
    'pgui.column_filter',
    'pgui.charts',
    'pgui.utils',
    'jquery.stickytableheaders'
], function (_, pageSettings, shortcuts, Grid, Filter, FilterBuilder, ColumnFilter, charts, utils) {

    function initGrids($grids) {
        $grids.each(function (i, el) {
            var $grid = $(el);
            var grid = new Grid($grid);
            var id = grid.getId();
            var gridData = window['gridData_' + id];

            // Filter builder
            var filterBuilder = grid.getFilterBuilder();
            _.each(gridData.filterBuilder.columns, function (columnData) {
                filterBuilder.addColumn(new Filter.Column(
                    columnData.fieldName,
                    columnData.caption,
                    columnData.operators
                ));
            });

            var filterBuilderGroup = new FilterBuilder.Group();
            filterBuilderGroup.deserialize(filterBuilder.getColumns(), gridData.filterBuilder.data);
            filterBuilder.setFilterComponent(filterBuilderGroup);

            // Column filter
            var columnFilter = grid.getColumnFilter();
            _.each(gridData.columnFilter.columns, function (columnData) {
                columnFilter.addColumn(new ColumnFilter.Column(
                    columnData.fieldName,
                    columnData.caption,
                    columnData.typeIsDateTime
                ));
            });

            var columnFilterGroup = new ColumnFilter.Group();
            columnFilterGroup.deserialize(columnFilter.getColumns(), gridData.columnFilter.data);
            columnFilter.setFilterComponent(columnFilterGroup);
            columnFilter.setDefaultsEnabled(gridData.columnFilter.isDefaultsEnabled);
            columnFilter.attach();

            // Quick filter
            var quickFilter = grid.getQuickFilter();
            quickFilter.setColumnNames(gridData.quickFilter.columns);
            quickFilter.highlight($grid);
            utils.updatePopupHints($grid.find('.pg-row'));
        });
    }

    return function () {
        var $body = $('body');

        pageSettings($body);

        var $tableToFixHeader = $('table.table.fixed-header');
        if ($tableToFixHeader.length > 0) {
            var $navbar = $('.navbar');
            var marginTop = 0;

            if ($navbar.hasClass('navbar-fixed-top')) {
                marginTop += $navbar.outerHeight();
            }

            $tableToFixHeader.stickyTableHeaders({
                selector: 'thead:first',
                marginTop: marginTop
            });
        }

        shortcuts.push(['grid']);

        initGrids($body.find('.js-grid'));
        charts.init($body);
    }
});

define('pgui.view-page-main',[
    'pgui.utils',
    'pgui.field-embedded-video',
    'pgui.cell-edit',
    'pgui.selection',
    'pgui.selection-handler',
    'pgui.image_popup',
    'pgui.modal_view'
], function(utils, showFieldEmbeddedVideo, initCellEdit, Selection, SelectionHandler, initImagePopup, initModalView) {

    return function () {
        var $body = $('body');

        utils.updatePopupHints($body);
        showFieldEmbeddedVideo($body);
        initImagePopup($body);


        $body.find('[data-edit-url]').each(function (i, el) {
            var $el = $(el);
            var columnName = $el.data('column-name');
            initCellEdit($el, function (response) {
                $el.html(response.columns[columnName]);
                $body.find('.js-message-container').append(
                    utils.buildDismissableMessage(
                        'success',
                        response.message,
                        response.messageDisplayTime
                    )
                );
            });
        });

        $body.find('[data-modal-operation=view]').each(function (i, el) {
            initModalView($(el));
        });

        var $selectionActions = $body.find('.js-selection-actions-container');
        if ($selectionActions.length) {
            var selectionHandler = new SelectionHandler(
                new Selection($selectionActions.data('selection-id')),
                $selectionActions
            );
        }
    }

});

define('pgui.compare-page-main',['pgui.view-page-main'], function(initViewPage) {
    return function () {
        initViewPage();

        function updateMode() {
            var $this = $(this);
            if ($this.prop('checked')) {
                $('[data-diff=false]').toggle($this.data('mode') === 'all');
            }
        }

        $('.js-compare-mode').on('change', updateMode).each(updateMode);
    }
});

define('pgui.form-page-main',['pgui.form_collection', 'pgui.shortcuts'], function(FormCollection, shortcuts) {
    return function () {
        var $formContainer = $('.js-form-container').first();
        var formCollection = new FormCollection(
            $formContainer,
            $('.js-form-collection'),
            $formContainer.data('form-url'),
            {
                done: function (hasErrors, responses, params) {
                    if (hasErrors) {
                        return true;
                    }

                    switch (params.action) {
                        case 'open':
                            location.href = params.url;
                            break;
                        case 'edit':
                            location.href = responses[0].editUrl;
                            break;
                        case 'details':
                            location.href = responses[0].details[params.index];
                            break;
                        default:
                            break;
                    }

                    return false;
                }
            }
        );
        shortcuts.push(['form']);
    }
});

define('pgui.user_management_api',[], function() {

    var PhpGenUserManagementApi = {

        _invokeApiFunction: function (functionShortName, data) {
            var result = new $.Deferred();

            $.get(
                'user_management_api.php',
                $.extend({},
                    {'hname': functionShortName},
                    data))
                .success(
                function (data) {
                    if (data.status == 'error')
                        result.reject(data.result);
                    else {
                        if (data.result)
                            result.resolve(data.result);
                        else
                            result.resolve();
                    }
                })
                .error(
                function (xhr, status, errorMessage) {
                    result.reject(status + ': ' + errorMessage);
                });

            return result.promise();
        },

        addUser: function (name, password) {
            return this._invokeApiFunction('au',
                {
                    'username': name,
                    'password': password
                });
        },

        addUserEx: function (name, password, email) {
            return this._invokeApiFunction('aue',
                {
                    'username': name,
                    'password': password,
                    'email': email
                });
        },

        updateUser: function (id, name, email, status) {
            return this._invokeApiFunction('uu',
                {
                    'user_id': id,
                    'username': name,
                    'email': email,
                    'status': status
                });
        },

        removeUser: function (id) {
            return this._invokeApiFunction('ru',
                {
                    'user_id': id
                });
        },

        changeUserName: function (id, name) {
            return this._invokeApiFunction('cun',
                {
                    'user_id': id,
                    'username': name
                });
        },

        changeUserPassword: function (id, password) {
            return this._invokeApiFunction('cup',
                {
                    'user_id': id,
                    'password': password
                });
        },

        getUserGrants: function (userId) {
            return this._invokeApiFunction('gug',
                {
                    'user_id': userId
                });
        },

        addUserGrant: function (userId, pageName, grantName) {
            return this._invokeApiFunction('aug',
                {
                    'user_id': userId,
                    'page_name': pageName,
                    'grant': grantName
                });
        },

        removeUserGrant: function (userId, pageName, grantName) {
            return this._invokeApiFunction('rug',
                {
                    'user_id': userId,
                    'page_name': pageName,
                    'grant': grantName
                });
        },

        selfChangePassword: function (currentPassword, newPassword) {
            return this._invokeApiFunction('self_change_password',
                {
                    'current_password': currentPassword,
                    'new_password': newPassword
                });
        }
    };

    return PhpGenUserManagementApi;

});

define('pgui.change_password_dialog',['pgui.utils', 'pgui.localizer', 'bootstrap'], function(utils, localizer) {

    window.PhpGenChangePasswordDialogAdminStrategy = function(onChangePasswordCallback) {
        this.onChangePasswordCallback = onChangePasswordCallback;

        this.initializeDialog = function () {
            $("#pg-change-password-dialog-header-admin").show();
            $("#pg-change-password-dialog-header-user").hide();
            $("#pg-change-password-dialog-current-password-form-group").hide();
        };

        this.callChangePasswordCallback = function (currentPassword, newPassword) {
            return this.onChangePasswordCallback(newPassword);
        };
    };

    window.PhpGenChangePasswordDialogUserStrategy = function(onChangePasswordCallback) {
        this.onChangePasswordCallback = onChangePasswordCallback;

        this.initializeDialog = function () {
            $("#pg-change-password-dialog-header-admin").hide();
            $("#pg-change-password-dialog-header-user").show();
            $("#pg-change-password-dialog-current-password-form-group").show();
        };

        this.callChangePasswordCallback = function (currentPassword, newPassword) {
            return this.onChangePasswordCallback(currentPassword, newPassword);
        };
    };

    window.PhpGenChangePasswordDialog = {

        initialize: function (userStrategy) {
            this.dialog = $("#pg-change-password-dialog");

            this.userStrategy = userStrategy;
            this.currentPassword = $("#pg-change-password-dialog-current-password");
            this.newPassword = $("#pg-change-password-dialog-new-password");
            this.confirmedPassword = $("#pg-change-password-dialog-confirmed-password");
            this.confirmedPasswordErrorControl = $("#pg-change-password-dialog-confirmed-password-error");

            this.dialog.modal('hide');
            this.userStrategy.initializeDialog();

            $("#pg-change-password-dialog-ok-button").off('click').on('click', function (e) {
                this.onChangePasswordClick(e);
            }.bind(this));
        },

        open: function () {
            this.clearPasswordElements();
            this.confirmedPasswordErrorControl.hide();
            this.dialog.modal('show');
            this.dialog.find('input:visible:first').focus();
        },

        clearPasswordElements: function () {
            this.currentPassword.val('');
            this.newPassword.val('');
            this.confirmedPassword.val('');
        },

        onChangePasswordClick: function (e) {
            e.preventDefault();

            if (!PhpGenPasswordDialogUtils.validateConfirmedPassword(
                    this.newPassword.val(), this.confirmedPassword.val(), this.confirmedPasswordErrorControl)) {
                return;
            }

            this.userStrategy.callChangePasswordCallback(this.currentPassword.val(), this.newPassword.val()).fail(
                function (message) {
                    utils.showErrorMessage(message);
                }).done(
                function () {
                    this.dialog.modal('hide');
                    utils.showSuccessMessage(localizer.getString('PasswordChanged'));
                }.bind(this));
        }
    };

});

