      $(field).val(wsVal).addClass('ws-changed-value');
      // $(field).val(wsVal).addClass('ws-changed-value').prop("disabled", false).
      //   find("option[value='" + wsValClean + "'][disabled]").prop("disabled", false).prop("selected", true);
