define('modal_customize',['bootstrap'], function () {

    var escapeHandler = function (e) {
        if (document.activeElement === document.body) {
            e.which == 27 && $('.modal').modal('hide');
        }
    }

    var $document = $(document);
    var origShow = $.fn.modal.Constructor.prototype.show;
    $.fn.modal.Constructor.prototype.show = function () {
        origShow.apply(this, arguments);

        var self = this;
        this.$element.one('shown.bs.modal', function () {
            var $input = self.$element.find('.form-control').get(0);
            if ($input) {
                $input.focus();
            }

            $document.on('keydown', escapeHandler);
        });

        this.$element.one('hidden.bs.modal', function () {
            $document.off('keydown', escapeHandler);
        });

    }

    var origHideModal = $.fn.modal.Constructor.prototype.hideModal;
    $.fn.modal.Constructor.prototype.hideModal = function () {
      var that = this;
      this.$element.hide();
      this.backdrop(function () {
        if (that.$body.find('.modal:visible').length === 0) {
            that.$body.removeClass('modal-open');
        }
        that.resetAdjustments();
        that.resetScrollbar();
        that.$element.trigger('hidden.bs.modal');
      })
    }

    $document.on('show.bs.modal', '.modal', function (event) {
        var parentModal = $document.find('.modal:visible').last();
        if (parentModal.length) {
            var parentWidth = parentModal.find('.modal-dialog').width();
            var $dialog = $(this).find('.modal-dialog');
            if (parentWidth === $dialog.width()) {
                $dialog.css('width', parentWidth - 40);
            }
        }

        var zIndex = 1040 + (10 * $('.modal:visible').length);
        $(this).css('z-index', zIndex);
        setTimeout(function() {
            $('.modal-backdrop').not('.modal-stack').css('z-index', zIndex - 1).addClass('modal-stack');
        }, 0);
    });
});

define('main',[
    'pgui.localizer',
    'pgui.sidebar',
    'pgui.list-page-main',
    'pgui.view-page-main',
    'pgui.compare-page-main',
    'pgui.form-page-main',
    'pgui.admin-page-main',
    'pgui.registration-page',
    'pgui.reset-password-page',
    'pgui.self_change_password',
    'pgui.utils',
    'pgui.user_management_api',
    'pgui.change_password_dialog',
    'pgui.password_dialog_utils',
    'jquery',
    'bootstrap',
    'custom/custom-require', // Afterburner
    'modal_customize'
], function (
    localizer,
    initSidebar,
    initListPage,
    initViewPage,
    initComparePage,
    initFormPage,
    initAdminPage,
    initRegistrationPage,
    initResetPasswordPage,
    initSelfChangePassword,
    utils
) {

    require(['user'], function () {

        if (typeof(window.beforePageLoad) === 'function') {
            window.beforePageLoad();
        }

        $(function () {
            var $body = $('body');
            var pageType = $body.data('page-entry');
            var inactivityTimeout = $body.data('inactivity-timeout');

            if (pageType != '' && inactivityTimeout > 0) {
                require(['inactivity_timeout'], function () {
                    initInactivityTimeout(inactivityTimeout);
                });
            }

            initSidebar($body);
            initSelfChangePassword();

            localizer.load().done(function () {
                switch (pageType) {
                    case 'list':
                        initListPage();
                        break;
                    case 'view':
                        initViewPage();
                        break;
                    case 'compare':
                        initComparePage();
                        break;
                    case 'form':
                        initFormPage();
                        break;
                    case 'admin':
                        initAdminPage();
                        break;
                    case 'register':
                        initRegistrationPage();
                        break;
                    case 'reset-password':
                        initResetPasswordPage();
                        break;
                }

                if ($body.data('inactivity-timeout-expired')) {
                    utils.showMessage(localizer.getString('InactivityTimeoutExpired'));
                }
            });

            if (typeof(window.afterPageLoad) === 'function') {
                window.afterPageLoad();
            }
        });

    });

});


require(["main"]);
